import React, {useEffect, useState} from 'react'


const ImageMars = () => {
    const [images, setImages] = useState([]);
    const [show, setShow] = useState(false);
    const [Photos, setPhotos] = useState()


    useEffect(() => {
        api();
    }, [])
    const api = async()=> {                                                                                                                                                                                                              
        const url = "https://api.nasa.gov/mars-photos/api/v1/rovers/curiosity/photos?sol=1000&api_key=2XmkxjQnMoob5xli1MzHey9cc5xQJxe6i8sVofNF";
        const res = await fetch(url);
        const data = await res.json();
        console.log(data)
        const dataShowImage = data.photos.slice(0,3).map( response => {
            return {
                id: response.id,
                Photos: response.img_src
            }
        });
        setImages(dataShowImage)
        console.log(dataShowImage)
    }

    function handleShow(Photos) {
        setPhotos(Photos)
        setShow(true);
    }

    const handleClose = () => {
        setShow(false);
        setPhotos(null);
    }


    return (
        <div>

            {
                images.slice(0, 4).map( ({ id, Photos }) => {
                    return (
                        // 
                        <div key={id}>
                            <img 
                            src={Photos} 
                            onclick={() => handleShow(Photos)}
                            alt="" />
                        </div>
                    )
                })
            }

            <div>
                <img src={Photos} alt="" />
            </div>

        </div>
    )
}

export default ImageMars
