import React, {useEffect, useState} from 'react'
import { Icon } from '@iconify/react';

const Weather = () => {

    const [weather, setWeather] = useState([]);   

    useEffect(() => {
        apiWeather();
    }, [])

    const apiWeather = async () => {

        const url = "https://mars.nasa.gov/rss/api/?feed=weather&category=msl&feedtype=json&date";
        const resp = await fetch( url );
        const data = await resp.json();


        const showData = data.soles.map( response => {
            return{
                id: response.id,
                sol: response.sol,
                datetime: response.terrestrial_date,
                tempMax: response.max_temp,
                tempMin: response.min_temp,
                sunrise: response.sunrise,
                sunset: response.sunset
            }
        })

        setWeather(showData);
        console.log(showData)
    }

    return (
        <section className="weather">
            <div className="weather__content">
                <article className="txt">
                    <ul>
                        <li>
                            <span>Latest Weather at <b>Gale Crater</b></span>
                        </li>
                    </ul>
                    {weather.slice(0,1).map( ({ id, sol, datetime, tempMax, tempMin, sunrise, sunset }) => {
                            return (
                                <ul key={id}>
                                    <li>
                                        <p><Icon icon="fa-solid:sun" color="white" width="30px" /> Sol <span>{sol}</span></p>
                                    </li>
                                    <li>
                                        <p>{datetime}</p>
                                    </li>
                                    <li>
                                        <p>High <span>{tempMax}° F | C</span></p>
                                    </li>
                                    <li>
                                        <p>Low <span>{tempMin}° F | C</span></p>
                                    </li>
                                    <li>
                                        <p>Sunrise <span>{sunrise} HR</span></p>
                                    </li>
                                    <li>
                                        <p>Sunset <span>{sunset} HR</span></p>
                                    </li>
                                </ul>
                            )

                        })
                    }

                </article>
            </div>
        </section>
    )
}

export default Weather
