import React, {useEffect, useState, useCallback} from 'react'
import ImageViewer from "react-simple-image-viewer";


const ImageMars = () => {

    const [currentImage, setCurrentImage] = useState(0);
    const [isViewerOpen, setIsViewerOpen] = useState(false);
    const [images, setImages] = useState([]);

    const openImageViewer = useCallback((index) => {
        setCurrentImage(id);
        setIsViewerOpen(true);
      }, []);

      const closeImageViewer = () => {
        setCurrentImage(0);
        setIsViewerOpen(false);
      };

    useEffect(() => {
        api();
    }, [])

    const api = async()=> {

        const url = "https://api.nasa.gov/mars-photos/api/v1/rovers/curiosity/photos?sol=1000&api_key=2XmkxjQnMoob5xli1MzHey9cc5xQJxe6i8sVofNF";
        const res = await fetch(url);
        const data = await res.json();
        console.log(data)
        const dataShowImage = data.photos.slice(0,3).map( response => {
            return {
                id: response.id,
                Photos: response.img_src
            }
        });
        setImages(dataShowImage)
        console.log(dataShowImage)
    }


    return (
        <div>

            {
                images.slice(0, 4).map( ({ id, Photos }) => {
                    return (
                        // 
                        <div >
                            <img 
                            src={Photos} 
                            onClick={() => openImageViewer(id)}
                            key={id}
                            alt="" />
                        </div>
                    )
                })
            }
            {isViewerOpen && (
                <ImageViewer
                src={images}
                currentIndex={currentImage}
                onClose={closeImageViewer}
                disableScroll={false}
                backgroundStyle={{
                    backgroundColor: "rgba(0,0,0,0.9)"
                }}
                closeOnClickOutside={true}
                />
            )}

        </div>
    )
}

export default ImageMars
