import React, {useEffect, useState} from 'react'
import Masonry, {ResponsiveMasonry} from "react-responsive-masonry";
import LazyLoad from 'react-lazyload';
import { Icon } from '@iconify/react';

const ImageMars = () => {
    const [images, setImages] = useState([]);
    const [show, setShow] = useState(false);
    const [Photos2, setPhotos] = useState();
    const [isOpened, setIsOpened] = useState(false);


    useEffect(() => {
        api();
    }, [])
    const api = async()=> {                                                                                                                                                                                                              
        const url = "https://api.nasa.gov/mars-photos/api/v1/rovers/curiosity/photos?sol=1000&api_key=2XmkxjQnMoob5xli1MzHey9cc5xQJxe6i8sVofNF";
        const res = await fetch(url);
        const data = await res.json();
        const dataShowImage = data.photos.slice(0, 50).map( response => {
            return {
                id: response.id,
                Photos: response.img_src
            }
        });
        setImages(dataShowImage)
    }

    const handleShow = (Photos) => {
        setPhotos(Photos)
        setShow(true);
        setIsOpened(true);
    }

    const handleClose = () => {
        setShow(false);
        setPhotos(null);
        setIsOpened(false);
    }


    return (
        <> 
            <section className="images-mars">
                <div className="images-mars__content">
                    <ResponsiveMasonry  columnsCountBreakPoints={{350: 1, 750: 2, 900: 3}}>
                        <Masonry gutter="10px">
                        {
                            images.map( ({ id, Photos }) => {
                                return (
                                    <div key={id}>
                                        <LazyLoad height={300} >
                                        <img 
                                        src={Photos} 
                                        onClick={() => handleShow(Photos)}
                                        style={{width: "100%", display: "block"}}
                                        alt="" />
                                    </LazyLoad>
                                    </div>
                                )
                            })
                        }
                        </Masonry> 
                    </ResponsiveMasonry> 
                </div>
            </section>
            { isOpened && (
                <div className="modal">
                    <div className="modal__content">
                        <div className="btn-close">
                            <a onClick={handleClose}><Icon icon="ep:close" color="white" height="60" /></a>
                        </div>
                        <img src={Photos2} alt="" />
                    </div>
                </div>
            )
            }
        </>
    )
}

export default ImageMars
