import React, {useEffect, useState} from 'react'
import Masonry, {ResponsiveMasonry} from "react-responsive-masonry"

const ImageMars = () => {
    const [images, setImages] = useState([]);
    const [show, setShow] = useState(false);
    const [Photos2, setPhotos] = useState()


    useEffect(() => {
        api();
    }, [])
    const api = async()=> {                                                                                                                                                                                                              
        const url = "https://api.nasa.gov/mars-photos/api/v1/rovers/curiosity/photos?sol=1000&api_key=2XmkxjQnMoob5xli1MzHey9cc5xQJxe6i8sVofNF";
        const res = await fetch(url);
        const data = await res.json();
        console.log(data)
        const dataShowImage = data.photos.slice(0, 18).map( response => {
            return {
                id: response.id,
                Photos: response.img_src
            }
        });
        setImages(dataShowImage)
        console.log(dataShowImage)
    }

    function handleShow(Photos) {
        setPhotos(Photos)
        setShow(true);
    }

    const handleClose = () => {
        setShow(false);
        setPhotos(null);
    }


    return (
        <div>

            <section className="imagesMars">

                <ResponsiveMasonry  columnsCountBreakPoints={{350: 1, 750: 2, 900: 3}}>
                    <Masonry gutter="10px">
                    {
                        images.map( ({ id, Photos }) => {
                            return (
                                // 
                                <div key={id}>
                                    <img 
                                    src={Photos} 
                                    onClick={() => handleShow(Photos)}
                                    style={{width: "100%", display: "block"}}
                                    alt="" />
                                </div>
                            )
                        })
                    }
                </Masonry> 
                </ResponsiveMasonry> 
            </section>

            <div>
                <img src={Photos2} alt="" />
            </div>
            <button type="button" onClick={handleClose}>Quitar</button>

        </div>
    )
}

export default ImageMars
