/* eslint-disable jsx-a11y/anchor-is-valid */
import React, {useRef, useEffect, useState} from 'react'
import { Link } from 'react-router-dom';
import { Icon } from '@iconify/react';

export const Header = () => {

    const [showNav, setShowNav] = useState(false);

    const header = useRef()
    useEffect(() => {
        window.onscroll = ()=> {
            if (window.scrollY > 0) {
                    header.current.classList.add('header-bg');                
                } else {
                    header.current.classList.remove('header-bg');  
                }
            }
    }, [])
    return (
        <header ref={header}>
            <nav className="nav-desk">
                <div className="logo-marsmellow">
                    <Link to="/">
                        <img src="https://res.cloudinary.com/underverse-clothing/image/upload/dpr_2.0,q_auto:best/v1651965315/Marsmellow/logo/logo-marsmellow-text-white.png" alt="" />
                    </Link>
                </div>
                <ul>
                    <li>
                        <Link to="/rovers">What is a Rover?</Link>
                    </li>
                    <li>
                        <a href="https://nasa.gov" target="_blank" rel="noreferrer">Documentation</a>
                    </li>
                    <li>
                        <a href="https://www.github.com/gerardojballesteros" target="_blank" rel="noreferrer">
                            <Icon icon="bi:github" color="white" width="23px" />
                        </a>
                    </li>
                </ul>
            </nav>
            <nav className="nav-mobile">
            <div className="logo-marsmellow">
                    <Link to="/">
                        <img src="https://res.cloudinary.com/underverse-clothing/image/upload/dpr_2.0,q_auto:best/v1651965315/Marsmellow/logo/logo-marsmellow-text-white.png" alt="" />
                    </Link>
                </div>
                {
                    showNav && (
                        <ul>
                            <li>
                                <Link to="/rovers">What is a Rover?</Link>
                            </li>
                            <li>
                                <a href="https://nasa.gov" target="_blank" rel="noreferrer">Documentation</a>
                            </li>
                            <li>
                                <a href="https://www.github.com/gerardojballesteros" target="_blank" rel="noreferrer">
                                    <Icon icon="bi:github" color="white" width="23px" />
                                </a>
                            </li>
                        </ul>
                    )
                }
                <div className="burger">
                    <Icon  icon="ph:circles-four-bold" color="white"  />
                </div>
            </nav>

        </header>
    )
}


export default Header
