import React, {useEffect, useState} from 'react'
import Masonry, {ResponsiveMasonry} from "react-responsive-masonry";
import LazyLoad from 'react-lazyload';
import { Icon } from '@iconify/react';
import 'animate.css';

const ImageMars = () => {
    const [images, setImages] = useState([]);
    const [show, setShow] = useState(false);
    const [Photos2, setPhotos] = useState();
    const [isOpened, setIsOpened] = useState(false);
    const [rovers, setRovers] = useState('curiosity');
    useEffect(() => {
        api();
    }, [rovers])
    const api = async()=> {                                                                                                                                                                                                              
        const url = `https://api.nasa.gov/mars-photos/api/v1/rovers/${rovers}/photos?sol=1000&api_key=2XmkxjQnMoob5xli1MzHey9cc5xQJxe6i8sVofNF`;
        const res = await fetch(url);
        const data = await res.json();
        const dataShowImage = data.photos.slice(0, 50).map( response => {
            return {
                id: response.id,
                Photos: response.img_src
            }
        });
        setImages(dataShowImage)
    }
    const handleShow = (Photos) => {
        setPhotos(Photos)
        setShow(true);
        setIsOpened(true);
    }
    const handleClose = () => {
        setShow(false);
        setPhotos(null);
        setIsOpened(false);
    }
    return (
        <> 
            <section className="images-mars">
                <div className="images-mars__content">
                    <article className="txt">
                        <h4>Mars as <strong>never seen before.</strong></h4>
                        <p>Through the Marsmellow app you will be able to see 
                            the latest images photographed on mars by the spirit, opportunity and curiosity rovers. 
                            Remember that these images are updated by the image bank of the <b>National Aeronautics Administration (Nasa)</b></p>
                    </article>

                        <h1>{rovers}</h1>
                        <button type="button" onClick={()=> setRovers('curiosity')}>Curiosity</button>
                        <button type="button" onClick={()=> setRovers('spirit')}>Spirit</button>

                    <ResponsiveMasonry className="gallery"  columnsCountBreakPoints={{350: 1, 750: 2, 900: 3}}>
                        <Masonry gutter="10px">
                        {
                            images.map( ({ id, Photos }) => {
                                return (
                                    <div key={id}>
                                        <LazyLoad height={300} class="animate__animated animate__fadeIn animate__delay-2s" >
                                        <img 
                                        src={Photos} 
                                        onClick={() => handleShow(Photos)}
                                        style={{width: "100%", display: "block", cursor: "pointer"}}
                                        alt="" />
                                    </LazyLoad>
                                    </div>
                                )
                            })
                        }
                        </Masonry> 
                    </ResponsiveMasonry> 
                </div>
            </section>
            { isOpened && (
                <div className="modal">
                    <div className="modal__content">
                        <div className="btn-close">
                            <a onClick={handleClose}><Icon icon="ep:close" color="white" /></a>
                        </div>
                        <img src={Photos2} alt="" />
                    </div>
                </div>
            )
            }
        </>
    )
}

export default ImageMars
