import React, {useRef, useEffect} from 'react'
import { Link } from 'react-router-dom';
import { Icon } from '@iconify/react';
import { gsap } from 'gsap'
import { ScrollTrigger } from 'gsap/ScrollTrigger';
gsap.registerPlugin(ScrollTrigger);

export const Header = () => {

    const header = useRef()

    useEffect(() => {

        const header = document.getElementsByTagName(header.current)

        function setHeader () {
            const scrollPoint = window.pageYOffset + headerHeight

            let blockPoint = 61 - (scrollPoint - secondBlock.offsetTop)
            if (blockPoint <= 0) { blockPoint = 0 }

            if (scrollPoint > secondBlock.offsetTop) {
              header.style.background = 'black'
            } else {
              header.style.background = 'transparent'
            }
            window.requestAnimationFrame(setHeader)
          }

          window.requestAnimationFrame(setHeader)


    }, [])

    return (
        <header ref={header}>
            <nav>
                <div className="logo-marsmellow">
                    <Link to="/">
                        <img src="https://res.cloudinary.com/underverse-clothing/image/upload/dpr_2.0,q_auto:best/v1651965315/Marsmellow/logo/logo-marsmellow-text-white.png" alt="" />
                    </Link>
                </div>
                <ul>
                    <li>
                        <Link to="/spirit">Spirit</Link>
                    </li>
                    <li>
                        <Link to="/opportunity">Opportunity</Link>
                    </li>
                    <li>
                        <Link to="/curiosity">Curiosity</Link>
                    </li>
                    <li>
                        <a href="https://nasa.gov" target="_blank">Documentation</a>
                    </li>
                    <li>
                        <a href="https://www.github.com/gerardojballesteros" target="_blank">
                            <Icon icon="bi:github" color="white" width="23px" />
                        </a>
                    </li>
                </ul>
            </nav>
        </header>
    )
}


export default Header
